					,#{sal}

						,#{deptid}



							,#{hiredate}


            				,sysdate


				)

mapper.update:
UPDATE emp
SET empname = #{emp.empname}
	,sal = #{emp.sal}
	,mgr = #{emp.mgr}
	,deptid = #{emp.deptid}
